import React from 'react';
import { withRouter } from 'next/router';
import { connect } from 'react-redux';
import { Dropdown, Menu, Segment, Grid, Container, Input, Icon, Button } from 'semantic-ui-react';
import Axios from 'axios';
import { FaExchangeAlt } from "react-icons/fa";

class Home extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            totalData: [],
            options: [],
            firstCurr: '',
            secondCurr: '',
            firstValue: 0,
            secondValue: 0,
            date: '',
            exchangeData: []
        }
    }

    componentDidMount() {
        console.log("header");
        const { totalData } = this.state;
        const options = [];

        let date = new Date();
        date.setDate(date.getDate() - 1);
        console.log("date ", date);
        date = date.toISOString().split('T')[0];
        console.log("after-date ", date);
        this.setState({
            date
        })
        Axios.get('https://restcountries.eu/rest/v2/all').then(({ data }) => {
            this.setState({
                totalData: data
            });
            data.forEach((country, i) => {
                options.push(
                    {
                        key: i, text: country.name + " " + "(" + country.currencies[0].code + ")", value: country.currencies[0].code
                    }
                )
            })
            this.setState({
                options
            })

        })
    }

    firstDD = (event, { value }) => {
        const { firstCurr, secondCurr,date } = this.state;
        console.log("firstDD", event.target, { value });
        this.setState({
            firstCurr: value
        })
        if ({value} !== '' && secondCurr !== '') {
            Axios.get(`https://api.exchangeratesapi.io/${date}?symbols=${value},${secondCurr}&base=${value}`
            ).then((res) => {
                console.log("res ", res);
                this.setState({
                    exchangeData: res.data
                })
            })
            .catch((error) => { console.log(error); });
        }
    }

        secondDD = (event, { value }) => {
            const { firstCurr, secondCurr,date } = this.state;
            console.log("secondDD", value);
            this.setState({
                secondCurr: value
            })

            if (firstCurr !== '' && {value} !== '') {
                Axios.get(
                    `https://api.ratesapi.io/api/2010-01-12?symbols=${firstCurr},${value}&base=${firstCurr}`
                    ).then((res) => {
                    console.log("res ", res);
                    this.setState({
                        exchangeData: res.data
                    })
                })
                .catch((error) => { console.log(error); });
            }
        }

        firstValue = (event) => {
            console.log("firstValue", event.target.value);
            this.setState({
                firstValue: event.target.value
            })
            const curr2 = this.state.secondCurr
            const calc1 = (event.target.value)*(this.state.exchangeData.rates.curr2)
        }

        secondValue = (event) => {
            console.log("secondValue", event.target.value);
            this.setState({
                secondValue: event.target.value
            })
        }

        render() {
            console.log("home")
            return (
                <Container>
                    <Segment.Group>
                        <Segment clearing inverted color="violet">
                            <Grid>
                                <Grid.Row>
                                    <Grid.Column textAlign="center" width={7}>
                                        <Dropdown placeholder="select" options={this.state.options} selection clearable search onChange={(e, { value }) => this.firstDD(e, { value })} />
                                    </Grid.Column>
                                    <Grid.Column textAlign="center" verticalAlign="middle" width={2}>
                                        <FaExchangeAlt />
                                    </Grid.Column>
                                    <Grid.Column textAlign="center" width={7}>
                                        <Dropdown placeholder="select" options={this.state.options} selection clearable search onChange={this.secondDD} />
                                    </Grid.Column>
                                </Grid.Row>
                            </Grid>
                        </Segment>
                        <Segment>
                            <Grid>
                                <Grid.Row>
                                    <Grid.Column textAlign="center" width={7}>
                                        <Input onChange={this.firstValue} />
                                    </Grid.Column>
                                    <Grid.Column textAlign="center" verticalAlign="middle" width={2}>
                                        <Button primary>Convert</Button>
                                    </Grid.Column>
                                    <Grid.Column textAlign="center" width={7}>
                                        <Input onChange={this.secondValue} />
                                    </Grid.Column>
                                </Grid.Row>
                            </Grid>
                        </Segment>
                    </Segment.Group>
                </Container>
            );
        }
    }
    const options = [
        { key: 1, text: 'Feet', value: 1 },
        { key: 2, text: 'Meter', value: 2 },
        { key: 3, text: 'Inch', value: 3 },
    ]
    const mapDispatchToProps = {};
    export default withRouter(Home);
